import axios from 'axios';
import React, { useId, useState } from 'react'

export default function TaskElement() {

    const todayDate = new Date(); 
    const formatDate = todayDate.getDate() < 10 ? `0${todayDate.getDate()}`:todayDate.getDate();
    const formatMonth = todayDate.getMonth() + 1 < 10 ? `0${todayDate.getMonth() + 1}`: todayDate.getMonth() + 1;
    const formattedDate = [todayDate.getFullYear(), formatMonth, formatDate].join('-');

    const [task, setTask] = useState({
        taskTitle: 'Leetcode',
        taskCategroy: '218. Two Sum',
        taskKeywords: 'Two Pointers, Array',
        taskTimer: 0,
        taskRevisionDate: formattedDate
    })

    const uniqueId = useId()

    const handleTaskCreation = (e) => {
        e.preventDefault()
        console.log({taskId: `${uniqueId}-${taskTitle}`, ...task})
        //axios.post('http://localhost:4000/api/createtasks', {})
    } 

  return (
    <div>
        <form onSubmit={handleTaskCreation}>
            <input 
                type='text' 
                placeholder='Task Title' 
                value={task.taskTitle} 
                onChange={e => setTask(
                    {
                        ...task,
                        taskTitle : e.target.value
                    }
                )}/>

            <input 
                type='text' 
                placeholder='Category' 
                value={task.taskCategroy}
                onChange={e => setTask(
                    {
                        ...task,
                        taskCategroy : e.target.value
                    }
                )}/>

            <input 
                type='text' 
                placeholder='keywords' 
                value={task.taskKeywords}
                onChange={e => setTask(
                    {
                        ...task,
                        taskKeywords : e.target.value
                    }
                )}/>

            <input 
                type='date' 
                placeholder='Next Revision Date' 
                value={task.taskRevisionDate}
                onChange={e => setTask(
                    {
                        ...task,
                        taskRevisionDate : e.target.value
                    }
                )}/>

            <button type='submit'>Submit</button>
        </form>
    </div>
  )
}
