import { useLayoutEffect, useState } from "react";
import GoogleAuthSignIn from "./components/GoogleAuthSignIn";
import { getAuth } from "firebase/auth";

function App() {
  const [isAuth, setAuthStatus] = useState(false || window.localStorage.getItem('authStatus') === true)

  const updateAuthStatus = (authValue) =>{
    setAuthStatus(authValue)
  }

  useLayoutEffect(()=>{
    getAuth().onAuthStateChanged((userCred) => {
      if(userCred){
        updateAuthStatus(true)
        window.localStorage.setItem('authStatus', true)
        userCred.getIdToken().then((token) => {
          console.log(token)
        })
      }
    })
  }, [isAuth])

  return (
    <div className="App">
        {isAuth? (
          <h1>User Logged In</h1>
        ): (
          <GoogleAuthSignIn authCheck={{'authStatus': isAuth, 'authStatusChanger': updateAuthStatus}} />
        )}
    </div>
  );
}

export default App;
