import React, { useEffect, useState } from 'react'
import axios from 'axios';

function TaskTimer({task, user}) {

    const [isTimerOn, setIsTimerOn] = useState(false)
    const [seconds, setSeconds] = useState(0)
    const [minutes, setMinutes] = useState(0)

    useEffect(()=>{
        if(isTimerOn == true){
            const timer = setInterval(()=>{
                setSeconds(seconds + 1)

                if(seconds == 59){
                    setMinutes(minutes + 1)
                    setSeconds(0)
                }

            }, 1000)

            return ()=>{
                clearInterval(timer)
            }
        }
    })

    const toMilliseconds = (min,sec) => (min*60+sec)*1000;


    const handlePlayButtonClick = (e) =>{
        setSeconds(0)
        setMinutes(0)
        setIsTimerOn(true)
    }

    const handleStopButtonClick = (e) =>{
        setIsTimerOn(false)
        console.log(`Stop Clicked on ${minutes}:${seconds}`)
        console.log(`In Milliseconds ${toMilliseconds(minutes, seconds)}`)
        task.taskTimer = task.taskTimer + toMilliseconds(minutes, seconds)
        // Update Task in Firestore
         const headers = {Authorization: `Bearer ${user.accessToken}`}
        axios
            .post('http://localhost:4000/api/updateTask', task, {headers: headers})
            .then(() => console.log("Updated Task"))
            .catch((e) => console.log(e.message))
    }

    const displayButtonByTimerStatus = () =>{
        if(isTimerOn){
            return (
                <>
                <span className='TaskCardTimerElement'>{minutes < 10? `0${minutes}` : minutes}:{seconds < 10? `0${seconds}`: seconds}</span>
                <button className='material-icons TaskCardPlayElement' onClick={handleStopButtonClick}>stop</button>
                </>
            )
        }else{
            return <button className='material-icons TaskCardPlayElement' onClick={handlePlayButtonClick}>play_arrow</button>
        }
    }

    return (
        <div className='TaskCardTimerContainer'>
            {displayButtonByTimerStatus()}
        </div>
    )
}

export default TaskTimer
