    } 

  return (
    <div>
        <form onSubmit={handleTaskCreation}>
            <input 
                type='text' 
                placeholder='Task Title' 
                value={task.taskTitle} 
                onChange={e => setTask(
                    ...task,
                    {taskTitle : e.target.value}
                )}/>

            <input type='text' placeholder='Category' value={task.taskCategroy}/>
            <input type='text' placeholder='keywords' value={task.taskKeywords}/>
            <input type='date' placeholder='Next Revision Date' value={task.taskRevisionDate}/>
            <button type='submit'>Submit</button>
        </form>
    </div>
  )
}
