import React, { useState } from 'react'

function TaskTimer() {

    const [isTimerOn, setIsTimerOn] = useState(false)

    const displayButtonByTimerStatus = () =>{
        if(isTimerOn){
            return (
                <>
                <span className='TaskCardTimerElement'>00:01</span>
                <button className='material-icons TaskCardPlayElement'>play_arrow</button>
                </>
            )
        }else{
            return <button className='material-icons TaskCardPlayElement'>play_arrow</button>
        }
    }

    return (
        <div className='TaskCardTimerContainer'>
            <span className='TaskCardTimerElement'>00:01</span>
            <button className='material-icons TaskCardPlayElement'>play_arrow</button>
        </div>
    )
}

export default TaskTimer
