import React, {useContext, useState} from 'react'

const AuthStatusContext = React.createContext()
const UpdateAuthStatusContext = React.createContext()

export default function AuthContext({ elements }) {
  const [isAuth, setAuthStatus] = useState(false)

  return (
    <AuthStatusContext.Provider value={isAuth}>
        <UpdateAuthStatusContext.Provider value={setAuthStatus}>
            {elements}
        </UpdateAuthStatusContext.Provider>
    </AuthStatusContext.Provider>
  )
}
