import React from 'react'
import '../styles/TaskCard.css'

function TaskCard({task}) {
  return (
    <div className='TaskCardContainer'>
      <p className='TaskCardCategoryElement'>{task.taskCategory}</p>
      <p className='TaskCardTitleElement'>{task.taskTitle}</p>
      <div className='TaskCardKeywordsContainer'>
        {task.taskKeywords.split(',').map((keyword)=>{
          if(keyword){
            return <p className='TaskCardKeywordElement'>{keyword}</p>
          }
        })}
      </div>
      <p className='TaskCardRevisionDateElement'>Next Revision Date</p>
      <div className='TaskCardDateElementContainer'><input classname='TaskCardDateElement' type='date' /></div>
      <div className='TaskCardTimerContainer'>
        <span className='TaskCardTimerElement'>00:01</span>
        <button className='material-icons TaskCardPlayElement'>play_arrow</button>
      </div>
    </div>
  )
}

export default TaskCard
