  const [isAuth, setAuthStatus] = useState(false)

  const updateAuthStatus = (authValue) =>{
    setAuthStatus(authValue)
  }

  useLayoutEffect(()=>{
    getAuth().onAuthStateChanged((userCred) => {
      if(userCred){
        updateAuthStatus(true)
        userCred.getIdToken().then((token) => {

        })
      }
    })
  }, [isAuth])


  const screenToShow = () => {
    if(isAuth){
      return <Home />
    }else{
      return <GoogleAuthSignIn authCheck={{'authStatus': isAuth, 'authStatusChanger': updateAuthStatus}} />
    }
  }
  return (
    <div className="App">
        {screenToShow()}
    </div>
  );
}

export default App;
