import React, { useEffect, useState } from 'react'
import {Bar, Line, Pie} from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import axios from 'axios'

Chart.register(...registerables);

function BarChartDaily({user, task}) {

    const [userElement, setUserElement] = useState(user)
    const [taskElement, setTaskElement] = useState(task)
    const [initialFetch, setInitialFetch] = useState(false)
    const [graphData, setGraphData] = useState({})

    const fetchDailyLogs = async () =>{
        setInitialFetch(true)
        const res = await axios.get('http://localhost:4000/api/getDailyLogs', {
            params: { userId: userElement.uid, taskId: taskElement.taskId },
            headers: {
                Authorization: `Bearer ${user.accessToken}`,
                'Content-Type': 'application/json',
            },
        })
        const labels = []
        const timeValues = []

        res.data.map((element)=>{
            labels.push(element.date)
            timeValues.push(((element.time / 60) / 60))
        })

        const colors=[]
        for(let i=0;i<labels.length;i++){
            colors.push('#'+Math.floor(Math.random()*16777215).toString(16));
        }


        const dataForGraph = {
            labels: labels,
            datasets: [
            {
                fill: true,
                label: task.taskTitle,
                backgroundColor: colors,
                borderColor: colors,
                data: timeValues,
            },
            ],
        }

        setGraphData(dataForGraph)

    }

    useEffect(()=>{
        if(initialFetch === false){
            fetchDailyLogs()
        }
    },[graphData])

    return (
        <>
            <div className='BarGraphContainer'>
                {(graphData.labels !== undefined)? <Bar options={{ maintainAspectRatio: false }} className="TaskAnalyticsDailyBarGraphElement" data={graphData} /> : null}
            </div>
            <div className='BarGraphContainer'>
                {(graphData.labels !== undefined)? <Line options={{ maintainAspectRatio: false }} className="TaskAnalyticsDailyBarGraphElement" data={graphData} /> : null}
            </div>
            <div className='BarGraphContainer'>
                {(graphData.labels !== undefined)? <Pie options={{ maintainAspectRatio: false }} className="TaskAnalyticsDailyBarGraphElement" data={graphData} /> : null}
            </div>
        </>
    )
}

export default BarChartDaily
